from decimal import Decimal


def info_from_operation(operation: list[dict]) -> list:
    sum_operation = 0
    info_card = {}
    sum_cashback = 0
    for item in operation:
        if int(item['Сумма операции']) < 0:
            sum_operation += Decimal(str(item['Сумма операции'])) * -1

            if item['Кэшбэк'] != '':
                sum_cashback += int(item['Кэшбэк'])
                info_card[item['Номер карты']] = {
                    'total_spent': sum_operation,
                    'cashback': sum_cashback}
            else:
                info_card[item['Номер карты']] = {
                    'total_spent': sum_operation,
                    'cashback': sum_cashback}
    list_info = []
    for k, v in info_card.items():
        list_info.append(
            {'last_digits': k, 'total_spent': info_card[k]['total_spent'], 'cashback': info_card[k]['cashback']})
    return list_info


